import React, {Component} from 'react';
import TopList from '../TopList/TopList';
import {Switch, Route} from 'react-router-dom';
import SimpleButton from '../UI/SimpleButton/SimpleButton';
import {Card} from '../UI/Card/Card';
import Projects from '../Projects/Projects';

const topListItems = [
  {link: '/projects/my', text: 'Мои проекты'},
  {link: '/projects/other', text: 'Чужие проекты'},
  {link: '/snippets', text: 'Сниппеты'},
  {link: '/components', text: 'Компоненты'},
];


class Home extends Component {

  constructor(props) {
    super(props); 

    this.state = {
      miniMenuIsOpen: false
    }

    this.toggleMiniMenu = this.toggleMiniMenu.bind(this);
  }

  toggleMiniMenu() {

    this.setState({
      miniMenuIsOpen: !this.state.miniMenuIsOpen
    });

  }

  render() {
    const miniListClass = this.state.miniMenuIsOpen ? 'mini-list active' : 'mini-list';

    return (
      <div className="content hidden-side">
      
        <Switch>
          <Route  path="/">
            <TopList menuButtonIsActive={this.state.miniMenuIsOpen} onMenuButtonClick={this.toggleMiniMenu} items={topListItems}>
              <div className={miniListClass}>
                <SimpleButton mixinClass="mini-list__item" dark>Cоздать проект</SimpleButton>
                <SimpleButton mixinClass="mini-list__item" dark>Cоздать компонент</SimpleButton>
                <SimpleButton mixinClass="mini-list__item" dark>Cоздать сниппет</SimpleButton>
              </div>
            </TopList>
          </Route>
          <Route path="/1">
            123
          </Route>
        </Switch>

      </div>
    );
  }
}

export default Home;
