import { PUT_REGISTER_DATA, PUT_FAILED_REGISTER_DATA, PUT_LOGIN_DATA, PUT_FAILED_LOGIN_DATA} from './actions';

const initialState = {
  token: '',
  success: false,
  message: false,
  errors: {},
  userName: 'user'
}

export const authReducer = (state = initialState, action) => {
  console.log(action.type);
  switch (action.type) {
    
    case PUT_REGISTER_DATA: return { 
      ...state, 
      success: action.payload.success,
      token: action.payload.data.token,
      userName: action.payload.data.name,
      message: action.payload.message,
      errors: {}
    }; 
    

    case PUT_FAILED_REGISTER_DATA: return {
      ...state, 
      success: action.payload.success,
      message: action.payload.message,
      errors: action.payload.data, 
      userName: 'user'
    }
    

    case PUT_LOGIN_DATA: return { 
      ...state, 
      success: true,
      token: action.payload.access_token,
      refreshToken: action.payload.refresh_token,
      userName: action.payload.data.name,
      errors: {}
    }; 

    case PUT_FAILED_LOGIN_DATA: return {
      ...state, 
       
      userName: 'user'
    }    

    default: return state;  
  }
}
