import { createStore, applyMiddleware } from 'redux';
import createSagaMiddleware from 'redux-saga';
import { watchRegisterData, watchLoginData } from './auth/sagas';
import reducers from './redusers';




const loadState = () => {
  try {
      const serialisedState = window.localStorage.getItem('app_state');

      if (!serialisedState) return {}; // todo: допилить

      return JSON.parse(serialisedState);
  } catch (err) {
      return undefined;
  }
};

const saveState = (state) => {
  try {
      const serialisedState = JSON.stringify(state);

      window.localStorage.setItem('app_state', serialisedState);
  } catch (err) {
    
  }
};

const sagaMiddleware = createSagaMiddleware();
const oldState = loadState();
const store = createStore(reducers, oldState, applyMiddleware(sagaMiddleware));

sagaMiddleware.run(watchRegisterData);
sagaMiddleware.run(watchLoginData);
  
store.subscribe(() => {
  saveState(store.getState());
});


export default store;
