import React, { Component } from 'react';
import './Card.sass';
import SimpleButton from '../SimpleButton/SimpleButton';

export class Card extends Component {
  render() {
    return (
        <div className="card">
          <CardTitle>Green cat - сайт защиты природы</CardTitle>
          <CardSubTitle>Участники:</CardSubTitle>
          
          <Members members={[{name: 'Oleg Koshobvich'}, {name: 'вы'}]} />

          <div className="card__buttons">
            <SimpleButton mixinClass="card__button" theme="purple">Открыть</SimpleButton>
            <SimpleButton mixinClass="card__button" theme="mint">Добавить участника</SimpleButton>
          </div>
        </div>
    );
  }
}

export const CardTitle = (props) => {
  return <h3 className="card__title">{props.children}</h3>
}

export const CardSubTitle = (props) => {
  return <h4 className="card__subtitle">{props.children}</h4>
}

export const Members = (props) => {
  return (
    <div className="card__members">
      {props.members.map((member) => {
        <div className="member">
          <a href="#" className="member__img">{member.name[0]}</a>
          <h5 className="member__name"><a href="#">{member.name}</a></h5>
        </div>
      })}
    </div>
  )
}
