import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';

import { Provider } from 'react-redux';
import store from "./store/index";

import './main.sass';

import {  
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from 'react-router-dom';

import PrivateRoute from './routs/PrivateRoute';

// pages 
import DefaultHome from './components/DefaultHome/DefaultHome';
import Project from './containers/Project';
import AuthContainer from './containers/AuthContainer';
import NotifyListContainer from './containers/NotifyListContainer';



function App () { 
  const [notify, setNotify] = useState([{text: 'first'}]);



  const updateNotify = (event) => {

    event.persist();
    const newNotify = [...notify, { text: 'text' }];
    setNotify(newNotify);
  }



  return (
    <Provider store={store}>
      <Router>
        <Switch>

          <PrivateRoute exact authed={store.getState().auth.success} path='/' component={() => home} />



          <Route path="/auth">
            {store.getState().auth.success && <Redirect to="/" />}
            <AuthContainer />
          </Route>

          <Route path="/project">
            <Project />
          </Route>  

        </Switch>
      </Router>

      <NotifyListContainer />
    </Provider>    
  );

}


export default App;
