import React, {Component, Fragment} from 'react';
import { Provider } from 'react-redux';
import { createStore, applyMiddleware } from 'redux';
import createSagaMiddleware from 'redux-saga';
import reducers from './store/redusers';

import './main.sass';

import {  
  BrowserRouter as Router,
  Switch,
  Route
} from 'react-router-dom';

import { watchRegisterData } from './store/auth/sagas';

// pages 
import DefaultHome from './components/DefaultHome/DefaultHome';
import Project from './containers/Project';
import LoginContainer from './containers/LoginContainer';
import RegisterContainer from './containers/RegisterContainer';



const loadState = () => {
  try {
      const serialisedState = window.localStorage.getItem('app_state');

      if (!serialisedState) return undefined;

      return JSON.parse(serialisedState);
  } catch (err) {
      return undefined;
  }
};


const sagaMiddleware = createSagaMiddleware();
const oldState = loadState();
const store = createStore(reducers, oldState, applyMiddleware(sagaMiddleware));

sagaMiddleware.run(watchRegisterData);

let isAuth = true;

const saveState = (state) => {
  try {
      const serialisedState = JSON.stringify(state);

      window.localStorage.setItem('app_state', serialisedState);
  } catch (err) {

  }
};

store.subscribe(() => {
  saveState(store.getState());
});




function App () {



  return (
    <Provider store={store}>
      <Router>
        <Switch>
          <Route path="/register">
            <RegisterContainer />
          </Route>

          <Route path="/login">
            <LoginContainer />
          </Route>

          <Route path="/project">
            <Project />
          </Route>  

          <Route path="/">
            {isAuth && <DefaultHome />}
            {!isAuth && 'hello mister anonim'}
          </Route>
        </Switch>
      </Router>
    </Provider>
  );

}

export default App;
