import { 
  PUT_GOTTEN_USER_PROJECTS,
  PUT_CREATED_USER_PROJECT,
  PUT_DELETED_USER_PROJECT,
  PUT_ADDED_USER_IN_PROJECT
} from './actions';

const initialState = {
  myProjects: {},
};

export const projectsReduser = (state = initialState, action) => {
  switch (action.type) {

    case PUT_GOTTEN_USER_PROJECTS: return ({
      ...state, 
      myProjects: action.payload
    });

    case PUT_CREATED_USER_PROJECT: return ({
      ...state, 
      myProjects: [state.myProjects, action.payload]
    });

    case PUT_DELETED_USER_PROJECT: return ({
      ...state, 
      myProjects: [state.myProjects, action.payload]
    });
    
    case PUT_ADDED_USER_IN_PROJECT: return ({
      ...state, 
      myProjects: {...state.myProjects, data: action.payload.data}
    });

    default: return state;  
  }
}
