import React, { Component } from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';

import DefaultHome from '../DefaultHome/DefaultHome';
import Register from './Register';
import Login from './Login';


class Auth extends Component {
  
  constructor(props) {
    super(props);
    this.state = { fields: {} };
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.name === 'isGoing' ? target.checked : target.value;
    const name = target.name;
    
    this.setState({
      fields: { ...this.state.fields, [name]: value },
      loading: false, 
      hasRequest: false,
    });
  }

  changeLoading() {
    this.setState({ loading: !this.props.loading });
  }

  registerOnSubmit(e) {
    e.preventDefault();
    this.changeLoading();
    this.props.sendRegisterData(this.state.fields);
  }
  
  loginOnSubmit(e) {
    e.preventDefault();
    this.changeLoading();
    this.props.sendLoginData(this.state.fields);
  }

  componentDidMount() {
    this.setState({
      thasRequest: false
    });
  }

  render() {

    return (
      <Switch>
        {this.props.sucess && <Redirect to="/"/>}
        <Route exact path='/auth'>
          <DefaultHome />
        </Route>
        <Route path='/auth/register'>
          <Register loading={this.state.loading} registerOnSubmit={this.registerOnSubmit.bind(this)} handleInputChange={this.handleInputChange.bind(this)}/>
        </Route>
        <Route path='/auth/login'>
          <Login loading={this.state.loading} loginOnSubmit={this.loginOnSubmit.bind(this)} handleInputChange={this.handleInputChange.bind(this)} />
        </Route>
      </Switch>
    );
  }
}



export default Auth;
