import React, {Component, Fragment} from 'react';
import { Provider } from 'react-redux';
import { createStore, applyMiddleware } from 'redux';
import createSagaMiddleware from 'redux-saga';
import reducers from './store/redusers';

import './main.sass';

import {  
  BrowserRouter as Router,
  Switch,
  Route
} from 'react-router-dom';

import { watchLoadData } from './store/auth/sagas';

// pages 
import DefaultHome from './components/DefaultHome/DefaultHome';
import Project from './containers/Project';
import LoginContainer from './containers/LoginContainer';
import RegisterContainer from './containers/RegisterContainer';

const sagaMiddleware = createSagaMiddleware();
const store = createStore(reducers, applyMiddleware(console.log('auth')));

sagaMiddleware.run(watchLoadData);

let isAuth = true;



function App () {



  return (
    <Provider store={store}>
      <Router>
        <Switch>

          <Route path="/login">
            <LoginContainer />
          </Route>

          <Route path="/project">
            <Project />
          </Route>

          <Route path="/register">
            <RegisterContainer />
          </Route>

          <Route path="/">
            {isAuth && <DefaultHome />}
            {!isAuth && 'hello mister anonim'}
          </Route>
        </Switch>
      </Router>
    </Provider>
  );

}

export default App;
