import React, { Component, Fragment } from 'react'; 
import ReactDOM from 'react-dom';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group'; 

import Notify from './Notify';

import './NotifyList.sass';

export default class NotifyList extends Component {
  render() {
    return ReactDOM.createPortal(
      <ReactCSSTransitionGroup transitionName="notify" transitionEnterTimeout={300} transitionLeaveTimeout={300}>
        <div onClick={this.props.onClick} className="modal-notify-wrap">
          {this.props.notify.map(item => <Notify key={new Date()}>{item.text}</Notify>)}
        </div>
      </ReactCSSTransitionGroup>,
      document.getElementById('modals')
    );
  }
}
