import React from 'react';
import {useParams} from 'react-router-dom';
import AceEditor from "react-ace";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCopy, faExpand } from '@fortawesome/free-solid-svg-icons'
import "ace-builds/src-noconflict/theme-twilight";
import "ace-builds/src-noconflict/theme-twilight";

import './Editor.sass';
import Loader from '../UI/Loader/Loader';



const Editor = (props) => {
  let { snippetId } = useParams();
  
  if (props.snippets.snippetsList.data) {
    const  snippet = props.snippets.snippetsList.data.filter(snippet => snippetId == snippet.id);
    console.log(snippet.text);
    return (
      <div className="editor" >
        <div id="editor-area" className="editor__area">
          <AceEditor
            mode="java"
            theme="twilight"
            value={snippet.text}
            name={snippet.id}
            editorProps={{ $blockScrolling: true }}
          />
        </div>
        <div className="editor__panel">
          <ul className="editor__controls">
            <li className="editor__control">
              <button className="editor__button">
                <FontAwesomeIcon icon={faExpand} />
              </button>
            </li>
            <li className="editor__control">
              <button className="editor__button">
                <FontAwesomeIcon icon={faCopy} />
              </button>
            </li>
          </ul>
        </div>
      </div>
    );
  } else {
    return <Loader />
  }
 
};

export default Editor;
