import { 
  PUT_GOTTEN_USER_PROJECTS,
  PUT_CREATED_USER_PROJECT,
} from './actions';

const initialState = {
  myProjects: [],
};

export const projectsReduser = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {

    case PUT_GOTTEN_USER_PROJECTS: return ({
      ...state, 
      myProjects: action.payload
    });

    case PUT_CREATED_USER_PROJECT: return ({
      ...state, 
      myProjects: [...state.myProjects, action.payload]
    });

    default: return state;  
  }
}
