import React, {Component} from 'react';
import './main.sass';
import Sidebar from './Sidebar/Sidebar.js'
import SnippetNav from './SnippetNav/SnippetNav.js'
import './variables.sass';

import TopicsScreen from './containers/TopicsScreen.js';

let snippets = [
  {type: 'html', id: '1'},
  {type: 'js', id: '2'}
];


class App extends Component {

  constructor(props) {
    super(props); 

    this.state = {
      snippets, 
      newSnippetType: ''
    }
  }

  addSnippet(e) {
    e.preventDefault();

    if (!this.state.newSnippetType.length > 1)
      return false;

    const newSnippet = {
      type: this.state.newSnippetType, 
      id: this.state.newSnippetType + Number(new Date()),
    }

    this.setState({
      snippets: this.state.snippets.concat(newSnippet),
    });

  }

  setNewSnippetType(e) {
    this.setState({ newSnippetType: e.target.value });
  }

  removeSnippet(e) {
    const id = e.currentTarget.attributes.id.value;
    const cleanedSnippets = this.state.snippets.filter(snippet => snippet.id != id); 

    console.log(id);
    if (window.confirm('Really?'))
      this.setState({snippets: cleanedSnippets});
