import React from 'react'; 
import './SimpleButton.sass';

export default function(props) {
  let className = props.className !== undefined ? 'simple-button ' + props.className : 'simple-button';

  if (props.width == 'wide') 
    className += ' simple-button_wide';
  
  if (props.loading) 
    className += ' simple-button_loading';


  switch (props.type) {
    case 'link': return(
      <a href={props.link} className={className}>{props.children}</a>
    );

    case 'submit': return(
      <button type="submit" className={className}>{props.children}</button>
    );

    default: return(
      <div className={className}>{props.children}</div>
    );
  }

  
}
