import React from 'react';
import {useParams} from 'react-router-dom';
import {useEffect} from 'react';
import Loader from '../UI/Loader/Loader';
import TopList from '../TopList/TopList';
import CreateSnippetForm from './CreateSnippetForm';

const Snippets = (props) => {
  
  let { componentId, projectId } = useParams();
  

  useEffect(() => {
    console.log('update');

    if (props.snippets.snippetsList.data === undefined || (props.snippets.snippetsList.data && props.snippets.snippetsList.data[0]['component_id'] == componentId) ) 
      props.getSnippets(componentId);
    
  
  });

  function openCreateComponentForm() {
    props.openPopup(
      <CreateSnippetForm 
        closePopup={props.closePopup}
        componentId={componentId}
        addNotify={props.addNotify}
        createSnippet={props.createSnippet}
      />
    );
  }

  return (
    <div>
      {props.snippets.snippetsList.data && <TopList linkGen={`{project${projectId}/component{id}}`} onMenuButtonClick={openCreateComponentForm} items={props.snippets.snippetsList.data} />}
      {!props.snippets.snippetsList.data && <Loader />}
    </div>
  );
};

export default Snippets;
