import React from 'react';

export default function MiniBlockInput(props) {
  if (props.type == 'select')
    return (
      <select name="select">
        {props.options.map(item => {
          console.log(item);
          return (
            <option name={item.text}>{item.text}</option>
          );
        })}
      </select>
    );

  return ( 
    <input onChange={props.onChange.bind(this)} value={props.newSnippetType} className="top-list__form-input bottom-line-input" type={props.type} placeholder={props.placeholder} />
  );
}
