import { takeEvery, put, call } from 'redux-saga/effects';
import { SEND_REGISTER_DATA, PUT_REGISTER_DATA, putRegisterData, sendAuthData } from './actions';
import * as config from '../config';


function fetchData(data) {
	
	try {
		console.log(JSON.stringify(data));

		return fetch(config.URL_REGISTER_API, { 
			method: 'POST', 
			body: JSON.stringify(data),
			headers: {
				'Content-Type': 'application/json'
			},
		})
			.then(response => response.ok ? response.json() : Promise.reject(response))
			.catch((e) => console.error(e));
	} catch(e) {
		console.log(e);
	}
	

}

function* workerRegisterData(action) {
	
		const data = yield call(fetchData, action.payload);
		console.log(data);
		yield put(putRegisterData(data));
	
	
	
	
}

export function* watchRegisterData() {
	yield takeEvery(SEND_REGISTER_DATA, workerRegisterData);
}

