import React, {Component} from 'react';
import Tag from '../UI/Tag/Tag.js'
import './SnippetNav.sass';


let snippets = [
  {type: 'html', id: '1', id: '1'},
  {type: 'js', id: '2', id: '2'}
];

class SnippetNav extends Component {
  constructor(props) {
    super(props); 

    this.state = {
      snippets, 
      newSnippetType: ''
    }
  }

  addSnippet(e) {
    e.preventDefault();

    if (!this.state.newSnippetType.length > 1)
      return false;

    this.setState({
      snippets: this.state.snippets.concat({
        type: this.state.newSnippetType, 
        id: Math.round(this.state.newSnippetType + Math.random() * 10),
      }),
      newSnippetType: ''
    });

    console.log('added snippet: ', {
      type: this.state.newSnippetType, 
      id: Math.round(this.state.newSnippetType + Math.random() * 10),
    });
  }

  setNewSnippetType(e) {
    this.setState({ newSnippetType: e.target.value });
  }

  

  render() {
    return(
      <nav class="top-content-nav">
      <ul class="top-list">
        {this.state.snippets.map(snippet => (<li class="top-list__item"><a class="top-list__link active" href="#">{snippet.type}</a></li>))}
        
        <li class="top-list__item">
          <a href="block:#add-snippet" class="top-list__button">+</a>

          <div id="add-snippet" class="top-list__add-block mini-block">
            <div class="mini-block__title">Добавить сниппет</div>
            
            <div class="tags">
              {this.state.snippets.map(snippet => (<Tag text={snippet.type}/>))} 
            </div>

            <form class="top-list__form" onSubmit={(e) => this.addSnippet(e)}>
              <input onChange={(e) => this.setNewSnippetType(e)} value={this.state.newSnippetType} class="top-list__form-input bottom-line-input" type="text" placeholder="Тип" />

              <div class="top-list__form-buttons">
                <button class="btn-grey btnclr close-block top-list__form-button">Отмена</button>
                <button type="submit" class="btn-grey btnclr top-list__form-button">Добавить</button>		
              </div>						
            </form>

          </div>
        </li>
      </ul>
      
    </nav>

    );
  }
}
export default SnippetNav;
