import React from 'react';
import {useParams} from 'react-router-dom';
import AceEditor from "react-ace";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCopy, faExpand } from '@fortawesome/free-solid-svg-icons'
import "ace-builds/src-noconflict/theme-twilight";

import './Editor.sass';

let { componentId, projectId } = useParams();

const Editor = (props) => {
  return (
    <div className="editor" >
      <div id="editor-area" className="editor__area">
        <AceEditor
          mode="java"
          theme="twilight"
          // value={}
          name="UNIQUE_ID_OF_DIV"
          editorProps={{ $blockScrolling: true }}
        />
      </div>
      <div className="editor__panel">
        <ul className="editor__controls">
          <li className="editor__control">
            <button className="editor__button">
              <FontAwesomeIcon icon={faExpand} />
            </button>
          </li>
          <li className="editor__control">
            <button className="editor__button">
              <FontAwesomeIcon icon={faCopy} />
            </button>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Editor;
