import React, {Component, Fragment, useState} from 'react';
import Tag from '../UI/Tag/Tag.js'
import MiniBlock from '../UI/MiniBlock/MiniBlock.js'
import { CSSTransition } from 'react-transition-group';

import './SnippetNav.sass';


class SnippetNav extends Component {
  
  constructor(props) {
    super(props);
    this.state = { show: false };
  }


  render() {
    return(
      <nav className="top-content-nav">
        <ul className="top-list">
              
                {this.props.snippets.map(snippet => (<SnippetNavItem type={snippet.type}/>))}
              
          
          
          
          <li className="top-list__item">
            <a href="block:#add-snippet" className="top-list__button">+</a>

            <MiniBlock id="add-snippet" className="top-list__add-block">
              <div className="mini-block__title">Добавить сниппет</div>
              
              <div className="tags">
                {this.props.snippets.map(snippet => (
                  <Tag onClick={this.props.removeSnippet} key={snippet.id} id={snippet.id} text={snippet.type}/>
                ))} 
              </div>

              <form className="top-list__form" onSubmit={(e) => this.props.addSnippet(e)}>
                <input onChange={(e) => this.props.setNewSnippetType(e)} value={this.props.newSnippetType} className="top-list__form-input bottom-line-input" type="text" placeholder="Тип" />

                <div className="top-list__form-buttons">
                  <button className="btn-grey btnclr close-block top-list__form-button">Отмена</button>
                  <button type="submit" className="btn-grey btnclr top-list__form-button">Добавить</button>		
                </div>						
              </form>
            </MiniBlock>
         
          </li>
        </ul>
      
      </nav>

    );
  }
}

class SnippetNavItem extends Component {

  constructor(props) {
    super(props);
    
    this.state = {
      show: false,
    }
  }

  componentWillLeave() {
    console.log('ща ливну');
  }

  render() {
    return (
      <CSSTransition timeout={200} in={this.show} classNames="tli" onEnter={() => this.componentWillLeave()} onExited={() => this.componentWillLeave()}>
        <li className="top-list__item">
          <a className="top-list__link active" href="#">{props.type}</a>
        </li>
      </CSSTransition>
    );
  }
}

export default SnippetNav;
