import React, {useState, useEffect} from 'react';
import {useParams} from 'react-router-dom';
import AceEditor from "react-ace";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCopy, faExpand, faTrash, faSave } from '@fortawesome/free-solid-svg-icons'
import "ace-builds/src-noconflict/theme-tomorrow_night";
import "ace-builds/src-noconflict/mode-javascript";

import './Editor.sass';
import Loader from '../UI/Loader/Loader';
import store from '../../store/index';


const Editor = (props) => {
  let { snippetId } = useParams();
  

  useEffect(() => {
    let timer;
    document.addEventListener('keydown', (e) => {
      if (e.ctrlKey && e.keyCode == 83) {
        e.preventDefault();
        clearTimeout(timer);

        timer = setTimeout(onSaveSnippet, 300);
      }
    });
    
  }, []);

  useEffect(() => {

  });

  function onSaveSnippet() {

    if (!store.getState().snippets.snippetsList.data)
      return false;
    
    const  snippet = store.getState().snippets.snippetsList.data.filter(snippet => snippetId == snippet.id)[0];
    props.onClickSaveButton({snippetId, data: {text: snippet.text}});
  }


  
  function onChangeEditor(value, snippet) {
    snippet.text = value;
  }

  if (props.snippets.snippetsList.data) {
    const  snippet = props.snippets.snippetsList.data.filter(snippet => snippetId == snippet.id)[0];
  
    return (
      <div className="editor" >
        <div id="editor-area" className="editor__area">
          <AceEditor
            mode="javascript"
            onChange={(value) => {onChangeEditor(value, snippet)}}
            theme="tomorrow_night"
            value={snippet.text || ''}
            name={'snippet_' + snippet.id}
            editorProps={{ $blockScrolling: true }}
            height='100%'
            width='100%'
            tabSize={2}
            fontSize='16px'
            placeholder='Вставте код, который хотите сохранить'
            style={{padding: '10px'}}
            setOptions={{
              enableBasicAutocompletion: true,
              enableLiveAutocompletion: true,
              enableSnippets: true
            }}
          />
        </div>
        <div className="editor__panel">
          <ul className="editor__controls">
            <li className="editor__control">
              <button className="editor__button">
                <FontAwesomeIcon icon={faExpand} />
              </button>
            </li>
            <li className="editor__control">
              <button className="editor__button">
                <FontAwesomeIcon icon={faCopy} />
              </button>
            </li>
            <li className="editor__control">
              <button className="editor__button">
                <FontAwesomeIcon icon={faTrash} />
              </button>
            </li>
            <li className="editor__control">
              <button className="editor__button" onClick={() => { props.onClickSaveButton({snippetId, data: {text: snippet.text}}) }}>
                <FontAwesomeIcon icon={faSave} />
              </button>
            </li>
          </ul>
        </div>
      </div>
    );
  } else {
    return <Loader />
  }
 
};

export default Editor;
