import {takeEvery, put, call} from 'redux-saga/effects';
import {
  GET_USER_PROJECTS,
  CREATE_USER_PROJECT,
  putGottenUserProjects,
  putCreatedUserProject
} from './actions';
import {addNotify} from '../notify/actions';
import * as config from '../config';
import store from '../index';

function fetchGetProjectsData(data) {
  const USER_TOKEN = store.getState().auth.token;

  return fetch(config.URL_PROJECT_API, { 
    method: 'GET', 
    headers: {      
      'Accept': 'application/json',
      'Content-Type': 'application/x-www-form-urlencoded',
      'Authorization': `Bearer ${USER_TOKEN}`
    },
  })
    .then(response => response.status == 404 || response.ok ? response.json() : Promise.reject(response))
    .catch((e) => console.error(e));
  
}

function fetchCreateProjectsData(data) {
  const USER_TOKEN = store.getState().auth.token;
  console.log(data);
  return fetch(config.URL_PROJECT_API, { 
    method: 'POST', 
    body: JSON.stringify(data),
    headers: {      
      'Accept': 'application/json',
      'Authorization': `Bearer ${USER_TOKEN}`
    },
  })
    .then(response => response.status == 404 || response.ok ? response.json() : Promise.reject(response))
    .catch((e) => console.error(e));
  
}

function* workerGetProjectsData(action) {
  
	const data = yield call(fetchGetProjectsData, action.payload);
  console.log(data);
	if (data.success) 	
    yield put(putGottenUserProjects(data));

}

function* workerCreateProjectsData(action) {
  
	const data = yield call(fetchCreateProjectsData, action.payload);
  console.log(data);
	if (data.success) 	
    yield put(putCreatedUserProject(data));

}


export function* watchGetProjectsData() {
  yield takeEvery(GET_USER_PROJECTS, workerGetProjectsData);
}

export function* watchCreateProjectsData() {
  console.log('watcher: ', CREATE_USER_PROJECT)
  yield takeEvery(CREATE_USER_PROJECT, workerCreateProjectsData);
}
