import React from 'react';
import {useParams} from 'react-router-dom';
import AceEditor from "react-ace";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCopy, faExpand, faTrash } from '@fortawesome/free-solid-svg-icons'
import "ace-builds/src-noconflict/theme-tomorrow_night";
import "ace-builds/src-noconflict/mode-javascript";

import './Editor.sass';
import Loader from '../UI/Loader/Loader';



const Editor = (props) => {
  let { snippetId } = useParams();
  
  if (props.snippets.snippetsList.data) {
    const  snippet = props.snippets.snippetsList.data.filter(snippet => snippetId == snippet.id)[0];
    console.log(snippet);
    return (
      <div className="editor" >
        <div id="editor-area" className="editor__area">
          <AceEditor
            mode="javascript"
            theme="tomorrow_night"
            value={snippet.text || ''}
            name={snippet.id}
            editorProps={{ $blockScrolling: true }}
            height='100%'
            width='100%'
            tabSize='2'
            fontSize='15rpx'
            placeholder='Вставте код, который хотите сохранить'
            setOptions={{
              enableBasicAutocompletion: true,
              enableLiveAutocompletion: true,
              enableSnippets: true
            }}
          />
        </div>
        <div className="editor__panel">
          <ul className="editor__controls">
            <li className="editor__control">
              <button className="editor__button">
                <FontAwesomeIcon icon={faExpand} />
              </button>
            </li>
            <li className="editor__control">
              <button className="editor__button">
                <FontAwesomeIcon icon={faCopy} />
              </button>
            </li>
            <li className="editor__control">
              <button className="editor__button">
                <FontAwesomeIcon icon={faTrash} />
              </button>
            </li>
          </ul>
        </div>
      </div>
    );
  } else {
    return <Loader />
  }
 
};

export default Editor;
