import React, { useState } from 'react';
import ReactDOM from 'react-dom';

import { Provider } from 'react-redux';
import store from "./store/index";

import './main.sass';

import {  
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from 'react-router-dom';


// pages 
import DefaultHome from './components/DefaultHome/DefaultHome';
import Project from './containers/Project';
import AuthContainer from './containers/AuthContainer';
import NotifyList from './components/UI/NotifyList/NotifyList';



function App () { 
  const [notify, setNotify] = useState([]);



  return (
    <Provider store={store}>
      <Router>
        <Switch>

          <Route exact path="/">
            {!store.getState().auth.success && <Redirect to="/auth" />}
            home
          </Route>

          <Route path="/auth">
            {store.getState().auth.success && <Redirect to="/" />}
            <AuthContainer />
          </Route>

          <Route path="/project">
            лучше бы я на сварщика пошел
          </Route>  

        </Switch>
      </Router>

      <NotifyList notify={notify} />
    </Provider>    
  );

}


export default App;
