import { 
  PUT_GOTTEN_COMPONENTS,
  PUT_CREATED_COMPONENT
} from './actions';

import store from '../index';

const initialState = {
  componentsList: {},
};




export const componentReduser = (state = initialState, action) => {
  switch (action.type) {
    case PUT_GOTTEN_COMPONENTS: 
      return (
        { ...state, componentsList: action.payload }
      );

    case PUT_CREATED_COMPONENT: 
      console.log(state.componentsList, 'asdfasdfasafsddfasfasd')
      return (
        {...state, componentsList: {...state.componentsList, data: [...state.componentsList.data, action.payload.data]}}
      );

    default: return state;  
  }
}
