import React, { Component, Fragment } from 'react';
import Sidebar from '../components/Sidebar/Sidebar.js';
import SnippetNav from '../components/SnippetNav/SnippetNav.js';

import * as _ from 'lodash';

const snippets = [
  { type: 'js', id: 0, value: 'code', componentId: 0 }
];

const components = [
  { 
    snippetName: 'name', 
    category: 'Шапки', 
    id: 0
  },
  { 
    snippetName: 'name', 
    category: 'Шапки', 
    id: 1
  },
  { 
    snippetName: 'футер', 
    category: 'Футеры', 
    id: 2
  },
  
];

const menuItems = _.keyBy(components, 'category');

console.log(menuItems);

class Project extends Component {

  constructor(props) {
    super(props);

    this.state = {
      snippets,
      sidebarIsOpen: true
    }
  }

  toggleSidebar() {
    this.setState({ sidebarIsOpen: !this.state.sidebarIsOpen });
  }

  addSnippet(e) {
    e.preventDefault();

    if (!this.state.newSnippetType.length > 1)
      return false;
    
    const newSnippet = {
      type: this.state.newSnippetType, 
      id: this.state.newSnippetType + Number(new Date()),
    }

    this.setState({
      snippets: this.state.snippets.concat(newSnippet),
    });

  }

  setNewSnippetType(e) {
    this.setState({ newSnippetType: e.target.value });
  }

  removeSnippet(e) {
    const id = e.currentTarget.attributes.id.value;
    const cleanedSnippets = this.state.snippets.filter(snippet => snippet.id != id); 
    
    console.log(id);
    if (window.confirm('Really?'))
      this.setState({snippets: cleanedSnippets});
  } 

  render() {
    const contentClass = this.state.sidebarIsOpen ? 'content' : 'content hidden-side';

    return (
      <Fragment>
        <Sidebar toggleSidebar={this.toggleSidebar.bind(this)} isOpen={this.state.sidebarIsOpen} menuItems={menuItems} />
        <div className={contentClass}>
          
          <SnippetNav snippets={this.state.snippets} 
            addSnippet={this.addSnippet.bind(this)}
            setNewSnippetType={this.setNewSnippetType.bind(this)}
            removeSnippet={this.removeSnippet.bind(this)}
            />

          <div className="snippets">
            <div className="snippet active">
              <div className="editor" id="html1">
                <div id="editor-area" className="editor__area"></div>
                <div className="editor__panel">
                  <ul className="editor__controls">
                    <li className="editor__control">
                      <button data-editor="html1" className="editor__button"><i className="fas fa-expand"></i></button>
                    </li>
                    <li className="editor__control">
                      <button className="editor__button"><i className="far fa-copy"></i></button>
                    </li>
                  </ul>
                </div>
              </div>
            </div>

            <div className="snippet">
              <div id="html" className="editor">
                <div id="editor-area" className="editor__area"></div>
                <div className="editor__panel">
                  <ul className="editor__controls">
                    <li className="editor__control">
                      <button data-editor="html" className="editor__button"><i className="fas fa-expand"></i></button>
                    </li>
                    <li className="editor__control">
                      <button className="editor__button"><i className="far fa-copy"></i></button>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </div>

        </div>
      </Fragment>
    );
  }

}

export default Project;
