import React, {Component} from 'react';
import TopList from '../TopList/TopList';
import {Switch, Route} from 'react-router-dom';
import SimpleButton from '../UI/SimpleButton/SimpleButton';
import {Card, CardClose, CardButtons, CardTitle} from '../UI/Card/Card';
import MiniButton from '../UI/MiniButton/MiniButton';
import {MyProjects} from '../Projects/MyProjects';

import CreateProjectForm from './CreateProjectForm';


const topListItems = [
  {link: '/my', text: 'Мои проекты'},
  {link: '/other', text: 'Чужие проекты'},
  {link: '/components', text: 'Компоненты'},
];


class Home extends Component {

  constructor(props) {
    super(props); 

    this.state = {
      miniMenuIsOpen: false
    }

    this.toggleMiniMenu = this.toggleMiniMenu.bind(this);
    this.onDeleteProject = this.onDeleteProject.bind(this);
    this.deleteProject = this.onDeleteProject.bind(this);
    this.openCreateProjectForm = this.openCreateProjectForm.bind(this);
  }

  openCreateProjectForm() {
    this.props.openPopup(
      <CreateProjectForm 
        createProject={this.props.createUserProject} 
        closePopup={this.props.closePopup}
        addNotify={this.props.addNotify}
      />
    );
  }

  toggleMiniMenu() {

    this.setState({
      miniMenuIsOpen: !this.state.miniMenuIsOpen
    });

  }
  
  deleteProject(projectId) {
    this.props.deleteUserProject(projectId);
    this.props.closePopup();
  }

  onDeleteProject(projectId) {
    this.props.openPopup(
      <Card>
        <CardClose onClick={this.props.closePopup} />
        <CardTitle>Вы точно хотите удалить проект?</CardTitle>
        <CardButtons>
          <MiniButton onClick={() => this.props.deleteProject(projectId)} red mixinClass="card__mini-button">Удалить</MiniButton>
          <MiniButton mixinClass="card__mini-button" onClick={this.props.closePopup}>Отмена</MiniButton>
        </CardButtons>
      </Card>
    );
  }

  render() {
    const miniListClass = this.state.miniMenuIsOpen ? 'mini-list active' : 'mini-list';

    return (
      <div className="content hidden-side">
        <TopList menuButtonIsActive={this.state.miniMenuIsOpen} onMenuButtonClick={this.toggleMiniMenu} items={topListItems}>
          <div className={miniListClass}>
            <SimpleButton onClick={this.openCreateProjectForm} mixinClass="mini-list__item" dark>Cоздать проект</SimpleButton>
            <SimpleButton mixinClass="mini-list__item" dark>Cоздать компонент</SimpleButton>
            <SimpleButton mixinClass="mini-list__item" dark>Cоздать сниппет</SimpleButton>
          </div>
        </TopList>
        <Switch>
          <Route path="/my">
            <MyProjects 
              onDeleteProject={this.onDeleteProject}
              myProjects={this.props.myProjects}
              getUserProjects={this.props.getUserProjects}
              />
          </Route>
        </Switch>
      </div>
    );
  }
}

export default Home;
