import React, { useState, useEffect } from 'react';

import { Provider } from 'react-redux';
import store from "./store/index";

import './main.sass';

import {  
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from 'react-router-dom';
import ModalContainer from './containers/ModalContainer';
import PrivateRoute from './routs/PrivateRoute';

// pages 
import DefaultHome from './components/DefaultHome/DefaultHome';
import Project from './containers/Project';
import AuthContainer from './containers/AuthContainer';
import NotifyListContainer from './containers/NotifyListContainer';
import HomeContainer from './containers/HomeContainer';



function App () {

  return (
    <Provider store={store}>
      <Router>
        <Switch>

          {/* <PrivateRoute exact store={store} path='/' component={Home} /> */}

          <Route path="/auth">
            {store.getState().auth.success && <Redirect to="/my" />}          
            <AuthContainer />
          </Route> 

          <Route path="/">

            <HomeContainer />
          </Route>

        </Switch>
      </Router>

      <ModalContainer />
      <NotifyListContainer />
    </Provider>    
  );

}


export default App;
