import React, {Component} from 'react';
import TopList from '../TopList/TopList';
import {Switch, Route} from 'react-router-dom';
import SimpleButton from '../UI/SimpleButton/SimpleButton';
import Projects from '../Projects/Projects';
import Project from '../../containers/Project';
import {Card} from '../UI/Card/Card';

const topListItems = [
  {link: '/', text: 'Все', exact: true},
  {link: '/my', text: 'Мои проекты'},
  {link: '/other', text: 'Чужие проекты'},
  {link: '/snippets', text: 'Сниппеты'},
  {link: '/components', text: 'Компоненты'},
];


class Home extends Component {

  constructor(props) {
    super(props); 

    this.state = {
      miniMenuIsOpen: false
    }

    this.toggleMiniMenu = this.toggleMiniMenu.bind(this);
    this.createProject = this.createProject.bind(this);
  }

  createProject() {
    this.props.openPopup(
      <Card>
        <CardTitle>Green cat - сайт защиты природы</CardTitle>
        <CardSubTitle>Участники:</CardSubTitle>
        
        <Members members={[{name: 'Oleg Koshobvich'}, {name: 'вы'}]} />

        <CardButtons>
          <SimpleButton mixinClass="card__button" theme="purple">Открыть</SimpleButton>
          <SimpleButton mixinClass="card__button" theme="mint">Добавить участника</SimpleButton>
        </CardButtons>
      </Card> 
    );
  }

  toggleMiniMenu() {

    this.setState({
      miniMenuIsOpen: !this.state.miniMenuIsOpen
    });

  }

  render() {
    const miniListClass = this.state.miniMenuIsOpen ? 'mini-list active' : 'mini-list';

    return (
      <div className="content hidden-side">
        <TopList menuButtonIsActive={this.state.miniMenuIsOpen} onMenuButtonClick={this.toggleMiniMenu} items={topListItems}>
          <div className={miniListClass}>
            <SimpleButton onClick={this.createProject} mixinClass="mini-list__item" dark>Cоздать проект</SimpleButton>
            <SimpleButton mixinClass="mini-list__item" dark>Cоздать компонент</SimpleButton>
            <SimpleButton mixinClass="mini-list__item" dark>Cоздать сниппет</SimpleButton>
          </div>
        </TopList>
        <Switch>
          <Route path="/">
            <Projects />
          </Route>
        </Switch>
      </div>
    );
  }
}

export default Home;
