import React, { Component } from 'react';
import TopListItem from './TopListItem';
import {Link} from 'react-router-dom';
import './TopList.sass';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons';
import logo from '../../img/logo.svg';

class TopList extends Component {
  linkGenerator (item) {
    let viewStr = this.props.linkGen;

    for (let key in item) {
      viewStr = viewStr.replace(`{${key}}`, item[key]);
    }
    return viewStr;
  }

  render() {
    const menuButtonClassName = this.props.menuButtonIsActive ? 'top-list__button active' : 'top-list__button';

    return (
      <nav className="top-content-nav">
        <ul className="top-list">
          
          {this.props.items.map(item => <TopListItem key={item.id} exact={item.exact} link={() => this.linkGenerator(item)}>{item.name}</TopListItem>)}
          
          <li className="top-list__item">
            <button onClick={this.props.onMenuButtonClick} className={menuButtonClassName}>+</button>            
          </li>
        </ul>

        {this.props.children}  
        <div className="top-content-nav__right">
          <Link className="logo-wrap" to="/"><img src={logo} alt="mycode"/></Link>
          <div className="main-user">
          <div className="main-user__img top-content-nav__user-img">{this.props.auth.user.name[0]}</div>
          </div>
          <ul className="user-menu top-content-nav__user-menu">
            <li className="user-menu__item"><Link className="user-menu__link">Профиль</Link></li>
            <li className="user-menu__item"><Link className="user-menu__link">Настройки</Link></li>
            <li className="user-menu__item user-menu__exit"><Link className="user-menu__link user-menu__exit-link">Выход <FontAwesomeIcon icon={faSignOutAlt}/></Link></li>
          </ul>
        </div>
      </nav>
    );
  }
}

export default TopList;
