import React, {useEffect} from 'react';
import Loader from '../UI/Loader/Loader';
import Projects from './Projects';
import {Card, CardClose, CardButtons} from '../UI/Card/Card';
import MiniButton from '../UI/MiniButton/MiniButton';

export const MyProjects = (props) => {

  const onClose = () => {
    this.props.openPopup();
  }

  useEffect(() => {
    if (props.myProjects.success === undefined)
      props.getUserProjects(
        <Card>
          <CardButtons>
            <CardClose />
            <MiniButton></MiniButton>
          </CardButtons>
        </Card>
      );    
  })

  if (props.myProjects !== undefined && props.myProjects.data !== undefined && props.myProjects.data.length !== 0) {
    return (
      <Projects 
        projectsList={props.myProjects}
        deleteUserProject={props.deleteUserProject}
        onСlose={props.deleteUserProject}
      />
    );
  } else if (props.myProjects.success !== undefined) {
    return 'Проектов пока нет :(, добавте новый';
  } else {
    return <Loader />;
  } 
}

export default MyProjects;
