import React from 'react';
import AceEditor from "react-ace";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCopy, faExpand } from '@fortawesome/free-solid-svg-icons'

import './Editror.sass';

const Editor = () => {
  return (
    <div className="editor" >
      <div id="editor-area" className="editor__area"></div>
      <div className="editor__panel">
        <ul className="editor__controls">
          <li className="editor__control">
            <button className="editor__button">
              <FontAwesomeIcon className="left-menu__remove-item" icon={faExpand} />
            </button>
          </li>
          <li className="editor__control">
            <button className="editor__button">
              <FontAwesomeIcon className="left-menu__remove-item" icon={faCopy} />
            </button>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Editor;
