import React, { useState } from 'react';

import { MiniBlock, MiniBlockTitle, MiniBlockInput, MiniBlockForm } from '../UI/MiniBlock/MiniBlock';
import './Auth.sass';
import SimpleButton from '../UI/SimpleButton/SimpleButton';

import logo from '../../img/logo.png';
import Notify from '../UI/Notify/Notify';



export default function Register(props) {

  return (
    <div className="auth-screen">
      
      <div className="auth-screen__logo">
        <img src={logo} alt=""/>
      </div>

      <MiniBlock className="auth-screen__form">
        <MiniBlockTitle>Регистрация</MiniBlockTitle>
        

        <MiniBlockForm onSubmit={ (e) => { props.registerOnSubmit(e) } }>
          <MiniBlockInput name="name" onChange={ props.handleInputChange.bind(this) } placeholder="Имя" />
          <MiniBlockInput name="email" onChange={ props.handleInputChange.bind(this) } placeholder="Email" />
          <MiniBlockInput name="password" onChange={ props.handleInputChange.bind(this) } type="password" placeholder="Пароль" />
          <MiniBlockInput name="c_password" onChange={ props.handleInputChange.bind(this) } type="password" placeholder="Повторите пароль" />

          <SimpleButton type="submit" className="auth-screen__form-button" width="wide">Зарегистрироваться</SimpleButton>
        </MiniBlockForm>
        
      </MiniBlock>
      {props.message && <Notify>{props.auth.message}</Notify>}
    </div>

    
  );
}
