import React, { Component, Fragment } from 'react';
import Sidebar from '../Sidebar/Sidebar';
import SnippetNav from '../SnippetNav/SnippetNav';

let snippets = [
  {type: 'html', value:'value', id: '1'},
  {type: 'js', value:'value', id: '2'}
];

const menuItems = [
  {link: '#', text: 'Шапки', parentId: 0, id: 1},
  {link: '#', text: 'Шапка 1', parentId: 1, id: 2},
  {link: '#', text: 'Шапка 2', parentId: 1, id: 3},
  {link: '#', text: 'Шапки 3', parentId: 1, id: 4},
  {link: '#', text: 'Футеры', parentId: 0, id: 5},
  {link: '#', text: 'Футер', parentId: 5, id: 6},
];

const projects = [
  { id: 0, name: 'Super project' }
];

class Project extends Component {

  constructor(props) {
    super(props);

    this.state = {
      snippets,
      sidebarIsOpen: true
    }
  }

  componentDidUpdate() {
    if (this.props.success) {
      this.props.history.push('/');
    }
  }

  
  toggleSidebar() {
    this.setState({ sidebarIsOpen: !this.state.sidebarIsOpen });
  }

  addSnippet(e) {
    e.preventDefault();

    if (!this.state.newSnippetType.length > 1)
      return false;
    
    const newSnippet = {
      type: this.state.newSnippetType, 
      id: this.state.newSnippetType + Number(new Date()),
    }

    this.setState({
      snippets: this.state.snippets.concat(newSnippet),
    });

  }

  setNewSnippetType(e) {
    this.setState({ newSnippetType: e.target.value });
  }

  removeSnippet(e) {
    const id = e.currentTarget.attributes.id.value;
    const cleanedSnippets = this.state.snippets.filter(snippet => snippet.id != id); 
    
