import { PUT_REGISTER_DATA, PUT_FAILED_REGISTER_DATA } from './actions';

const initialState = {
  token: '',
  success: false,
  message: false,
  errors: {},
  userName: 'user'
}

export const authReducer = (state = initialState, action) => {
  console.log(action.type);
  switch (action.type) {
    
    case PUT_REGISTER_DATA: 
      return { 
        ...state, 
        success: action.payload.success,
        token: action.payload.data.token,
        userName: action.payload.data.name,
        errors: {}
      }; 
      break;

    case PUT_FAILED_REGISTER_DATA: 
      return {
        ...state, 
        success: action.payload.success,
        message: action.payload.message,
        errors: action.payload.data, 
        userName: 'user'
      };
      break;

    

    default: return state;

    
  }
}
