import { PUT_REGISTER_DATA, PUT_AUTH_DATA } from './actions';

const initialState = {
  isAuthorized: false,
  userToken: '',
  userName: 'user',
}

export const authReducer = (state = initialState, action) => {
  console.log(action.payload);
  switch (action.type) {
    
    case PUT_REGISTER_DATA: return { 
      ...state, 
      userToken: action.payload.data.token,
      userName: action.payload.data.name,
      isAuthorized: true
    }

    default: return state;
  }
}
