import React, {Component} from 'react';
import {useParams} from 'react-router-dom';
import AceEditor from "react-ace";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCopy, faExpand, faTrash, faSave } from '@fortawesome/free-solid-svg-icons'
import "ace-builds/src-noconflict/theme-tomorrow_night";
import "ace-builds/src-noconflict/mode-javascript";
import {matchPath} from 'react-router-dom';
import './Editor.sass';
import Loader from '../UI/Loader/Loader';
import store from '../../store/index';


class Editor extends Component {

  constructor(props) {
    super(props); 

    this.state = {
      snippetId: ''
    };
  }
  
  componentDidMount() {
    this.bindSaveKeys();
  }

  bindSaveKeys() {
    let timer;
    document.addEventListener('keydown', (e) => {
      if (e.ctrlKey && e.keyCode == 83) {
        e.preventDefault();
        clearTimeout(timer);

        timer = setTimeout(this.onSaveSnippet.bind(this), 300);
      }
    });
  }

  onSaveSnippet() {
    console.log(this.match)
    if (!store.getState().snippets.snippetsList.data)
      return false;
    // this.props.onClickSaveButton({this.match.params.snippetId, data: {text: snippet.text}});
  }


  
  onChangeEditor(value, snippet) {
    snippet.text = value;
  }
 
  render() {
    this.match = matchPath(this.props.location.pathname, {
      path: '/project:projectId/component:componentId/snippet:snippetId'
    });

    let snippetId = this.match.params.snippetId;

    if (this.state.snippetId != snippet.id) 
      this.setState({snippetId: this.match.params.snippetId});
      console.log(this.match.params.snippetId);


    if (this.props.snippets?.snippetsList?.data) {
      const  snippet = this.props.snippets.snippetsList.data.filter(snippet => this.match.params.snippetId == snippet.id)[0];
      
    
      return (
        <div className="editor" >
          <div id="editor-area" className="editor__area">
            <AceEditor
              mode="javascript"
              onChange={(value) => {this.onChangeEditor(value, snippet)}}
              theme="tomorrow_night"
              value={snippet.text || ''}
              name={'snippet_' + snippet.id}
              editorProps={{ $blockScrolling: true }}
              height='100%'
              width='100%'
              tabSize={2}
              fontSize='16px'
              placeholder='Вставте код, который хотите сохранить'
              style={{padding: '10px'}}
              setOptions={{
                enableBasicAutocompletion: true,
                enableLiveAutocompletion: true,
                enableSnippets: true
              }}
            />
          </div>
          <div className="editor__panel">
            <ul className="editor__controls">
              <li className="editor__control">
                <button className="editor__button">
                  <FontAwesomeIcon icon={faExpand} />
                </button>
              </li>
              <li className="editor__control">
                <button className="editor__button">
                  <FontAwesomeIcon icon={faCopy} />
                </button>
              </li>
              <li className="editor__control">
                <button className="editor__button">
                  <FontAwesomeIcon icon={faTrash} />
                </button>
              </li>
              <li className="editor__control">
                <button className="editor__button" onClick={() => { this.props.onClickSaveButton({snippetId: this.match.params.snippetId, data: {text: snippet.text}}) }}>
                  <FontAwesomeIcon icon={faSave} />
                </button>
              </li>
            </ul>
          </div>
        </div>
      );
      } else {
        return <Loader />
      }
  }
 
};

export default Editor;
