import { 
  PUT_GOTTEN_USER_PROJECTS,
  PUT_CREATED_USER_PROJECT,
} from './actions';

const initialState = {
  projectsList: [],
  projectsIsGetted: false,
};

export const projectsReduser = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {

    case PUT_GOTTEN_USER_PROJECTS: return ({
      ...state,
      projectIsGetted: true, 
      projectsList: action.payload.data
    });

    case PUT_CREATED_USER_PROJECT: return ({
      ...state,
      projectIsGetted: true, 
      projectsList: [...state.projectsList, action.payload] 
    });

    default: return state;  
  }
}
