import { takeEvery, put, call } from 'redux-saga/effects';
import { SEND_REGISTER_DATA, putRegisterData } from './actions';

const URL_REGISTER_API = 'http://mycode/api/register' 


function fetchData(data) {
  const parameters = {
    name: data.name,
    email: data.email,
    password: data.password,
    c_password: data. c_password
  }

  console.log(data);

  try {
    return fetch(URL_REGISTER_API, { method: 'POST', body: JSON.stringify(parameters) })
      .then(response => response.json());
  } catch (error) {
    throw new console.error(error);
  }

}

function* workerRegisterData(action) {
  
  const data = yield call(fetchData, action.payload);
  console.log(data);
  // yield put(putRegisterData(data));
}

export function* watchRegisterData() {
  yield takeEvery(SEND_REGISTER_DATA, workerRegisterData);
}
