import React, {Component} from 'react';
import TopList from '../TopList/TopList';
import {Switch, Route} from 'react-router-dom';
import {Card, CardClose, CardButtons, CardTitle} from '../UI/Card/Card';
import MiniButton from '../UI/MiniButton/MiniButton';
import {MyProjects} from '../Projects/MyProjects';
import {OtherProjects} from '../Projects/OtherProjects';
import CreateProjectForm from './CreateProjectForm';
import AddUserInProjectForm from './AddUserInProjectForm';
import Project from '../Project/Project';

const topListItems = [
  {link: '/', exact: true, text: 'Мои проекты'},
  {link: '/other', text: 'Чужие проекты'},
];


class Home extends Component {

  constructor(props) {
    super(props); 

    this.state = {
      miniMenuIsOpen: false
    }

    this.toggleMiniMenu = this.toggleMiniMenu.bind(this);
    this.onDeleteProject = this.onDeleteProject.bind(this);
    this.deleteProject = this.deleteProject.bind(this);
    this.openCreateProjectForm = this.openCreateProjectForm.bind(this);
    this.openAddUserInProjectForm = this.openAddUserInProjectForm.bind(this);
    this.onRemoveUserInProject = this.onRemoveUserInProject.bind(this);
    this.deleteUserInProject = this.deleteUserInProject.bind(this);
  }

  openCreateProjectForm() {
    this.props.openPopup(
      <CreateProjectForm 
        createProject={this.props.createUserProject} 
        closePopup={this.props.closePopup}
        addNotify={this.props.addNotify}
      />
    );
  }

  openAddUserInProjectForm(projectId) {
    this.props.openPopup(
      <AddUserInProjectForm 
        projectId={projectId}
        addUserInProject={this.props.addUserInProject} 
        closePopup={this.props.closePopup}
        addNotify={this.props.addNotify}
      />
    );
  }

  toggleMiniMenu() {

    this.setState({
      miniMenuIsOpen: !this.state.miniMenuIsOpen
    });

  }
  
  deleteProject(projectId) {
    this.props.deleteUserProject(projectId);
    this.props.closePopup();
  }

  deleteUserInProject(projectId, userId) {
    this.props.deleteUserInProject(userId, projectId)
    this.props.closePopup();
  }

  onDeleteProject(projectId) {
    this.props.openPopup(
      <Card>
        <CardClose onClick={this.props.closePopup} />
        <CardTitle>Вы точно хотите удалить проект?</CardTitle>
        <CardButtons>
          <MiniButton onClick={() => this.deleteProject(projectId)} red mixinClass="card__mini-button">Удалить</MiniButton>
          <MiniButton mixinClass="card__mini-button" onClick={this.props.closePopup}>Отмена</MiniButton>
        </CardButtons>
      </Card>
      
    );
  }
  
  onRemoveUserInProject(projectId, userId, event) {
    event.preventDefault();
    this.props.openPopup(
      <Card>
        <CardClose onClick={this.props.closePopup} />
        <CardTitle>Вы точно хотите удалить пользователя из проекта?</CardTitle>
        <CardButtons>
          <MiniButton onClick={() => {this.deleteUserInProject(projectId, userId)}} red mixinClass="card__mini-button">Удалить</MiniButton>
          <MiniButton mixinClass="card__mini-button" onClick={this.props.closePopup}>Отмена</MiniButton>
        </CardButtons>
      </Card>
      
    );
  }

  render() {
    const miniListClass = this.state.miniMenuIsOpen ? 'mini-list active' : 'mini-list';

    return (
      <div className="content hidden-side">
        
          
          <Switch>
            <Route exact path="/">
            <TopList menuButtonIsActive={this.state.miniMenuIsOpen} onMenuButtonClick={this.openCreateProjectForm} items={topListItems} />
              <MyProjects 
                onDeleteProject={this.onDeleteProject}
                onRemoveUserInProject={this.onRemoveUserInProject}
                openAddUserInProjectForm={this.openAddUserInProjectForm}
                myProjects={this.props.myProjects}
                getUserProjects={this.props.getUserProjects}
                />
            </Route>
            <Route path="/other">
            <TopList menuButtonIsActive={this.state.miniMenuIsOpen} onMenuButtonClick={this.openCreateProjectForm} items={topListItems} />
              <OtherProjects 
                onDeleteProject={this.onDeleteProject}
                onRemoveUserInProject={this.onRemoveUserInProject}
                openAddUserInProjectForm={this.openAddUserInProjectForm}
                otherProjects={this.props.otherProjects}
                getOtherProjects={this.props.getOtherProjects}
                />
            </Route>

            <Route path="/project/:id">
              {this.props.match.params.id}
              <Project {...this.props} />
            </Route>

            <Route path="/jopa/:id">
              jopa - {this.props.match.params.id}
            </Route>

          </Switch>
      </div>
    );
  }
}

export default Home;
