import { ADD_NOTIFY } from './actions';

const initialState = {
  notifyList: []
};

export const notifyReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_NOTIFY: return ({
      ...state, 
      notifyList: action.payload
    });

    default: return state;  
  }
}
