import React, { Component } from 'react';
import { Switch, Route, } from 'react-router-dom';

import DefaultHome from '../DefaultHome/DefaultHome';
import Register from './Register';
import Login from './Login';

function handleInputChange(event, setState, fields) {
  const target = event.target;
  const value = target.name === 'isGoing' ? target.checked : target.value;
  const name = target.name;

  setState({
    data: { ...fields.data, [name]: value }
  });
}



class Auth extends Component {
  
  constructor(props) {
    super(props);
    this.state = { fields: {} };
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.name === 'isGoing' ? target.checked : target.value;
    const name = target.name;
  
    this.setState({
      fields: { ...this.state.fields, [name]: value }
    });
  }

  registerOnSubmit(e) {
    e.preventDefault();
    this.props.sendRegisterData(this.state.fields.data);
  }

  authOnSubmit(e) {
    e.preventDefault();
    // ...
  }
  
  render() {

    return (
      <Switch>
        <Route exact path='/auth'>
          <DefaultHome />
        </Route>
        <Route path='/auth/register'>
          <Register registerOnSubmit={this.registerOnSubmit.bind(this)} handleInputChange={this.handleInputChange.bind(this)} />
        </Route>
        <Route path='/auth/login'>
          <Login handleInputChange={this.handleInputChange.bind(this)} />
        </Route>
      </Switch>
    );
  }
}



export default Auth;
