import React, { useState } from 'react';

import { MiniBlock, MiniBlockTitle, MiniBlockInput, MiniBlockForm } from '../UI/MiniBlock/MiniBlock';
import './Auth.sass';
import SimpleButton from '../UI/SimpleButton/SimpleButton';

import logo from '../../img/logo.png';

export default function Login() {
  return (
    <div className="auth-screen">
      
      <div className="auth-screen__logo">
        <img src={logo} alt=""/>
      </div>

      <MiniBlock className="auth-screen__form">
        <MiniBlockTitle>Вход</MiniBlockTitle>
        

        <MiniBlockForm>
          <MiniBlockInput onChange={ props.handleInputChange.bind(this) } placeholder="Логин" />
          <MiniBlockInput onChange={ props.handleInputChange.bind(this) } placeholder="Пароль" />

          <SimpleButton className="auth-screen__form-button" width="wide">Войти</SimpleButton>
        </MiniBlockForm>
        
      </MiniBlock>
    </div>
  );
}
