import React, { Component } from 'react';
import './Sidebar.sass';
import './_sidebar.js';
import SidebarItem from './SidebarItem.js';
import $ from 'jquery';

const menuItems = [
  {link: '#', text: 'Шапки', parentId: 0, id: 1},
  {link: '#', text: 'Шапка 1', parentId: 1, id: 2},
  {link: '#', text: 'Шапка 2', parentId: 1, id: 3},
  {link: '#', text: 'Шапки 3', parentId: 1, id: 4},
  {link: '#', text: 'Футеры', parentId: 0, id: 5},
  {link: '#', text: 'Футер', parentId: 5, id: 6},
];

class Sidebar extends Component {

  toggleSidebar() {
    $('.content').toggleClass('hidden-side');
    
    $(this.Sidebar).toggleClass('hidden');
  }

  render(props) {
    return (
      <aside className="sidebar" ref={aside => this.Sidebar = aside}>
        
        <div className="sidebar__hover-line" onClick={() => this.toggleSidebar()}></div>
        
        <div className="sidebar__head">
          <span className="sidebar__pre-title">
            Project:
          </span>
          <h1 className="sidebar__title">Future project</h1>
        </div>

        <nav className="left-menu">

          <ul className="left-menu__list">
            {menuItems.map(item => {
              const childs = menuItems.filter(_item => item.id == _item.parentId );
              if (item.parentId == 0)
                return (
                  <SidebarItem item={item} childs={childs} key={item.id}/>
                );
            })}
          </ul>

        </nav>

        <div className="sidebar__button" onClick={() => this.toggleSidebar()}>
          <svg className="sidebar__button-icon" xmlns="http://www.w3.org/2000/svg" width="10.567" height="16.95" viewBox="0 0 10.567 16.95">
            <path id="Path_3" data-name="Path 3" d="M314.383,530.331l-8.4,8.058,8.4,7.422" transform="translate(315.076 546.56) rotate(180)" fill="none" stroke="#918f8f" stroke-linecap="round" stroke-linejoin="round" stroke-width="2"/>
          </svg>
          
        </div>

        <div className="sidebar__bottom">
          <a href="block:#add-component" className="btnclr button-add sidebar__add">
            Добавить компонент<i className="button-add__icon fas fa-plus"></i>
            
          
          </a>
          <div className="sidebar__add-block mini-block">
            <div className="mini-block__title">Добавить сниппет</div>
              
              <div className="tags">
                <div className="tag">html<i className="tag__icon fas fa-times"></i></div>
                <div className="tag">js<i className="tag__icon fas fa-times"></i></div>
                <div className="tag">css<i className="tag__icon fas fa-times"></i></div>
              </div>

              <form className="top-list__form">
                <input className="top-list__form-input bottom-line-input" type="text" placeholder="Название" />

                <div className="top-list__form-buttons">
                  <button className="btn-grey btnclr close-block top-list__form-button">Отмена</button>
                  <button className="btn-grey btnclr top-list__form-button">Добавить</button>		
                </div>						
              </form>
            </div>

          </div>

        </aside>
    );

  }
}


export default Sidebar;
