import { 
  PUT_GOTTEN_USER_PROJECTS,
  PUT_CREATED_USER_PROJECT,
  PUT_DELETED_USER_PROJECT,
  PUT_ADDED_USER_IN_PROJECT
} from './actions';

const initialState = {
  myProjects: {},
};

export const projectsReduser = (state = initialState, action) => {console.log(action.payload);
  switch (action.type) {

    case PUT_GOTTEN_USER_PROJECTS: return ({
      ...state, 
      myProjects: action.payload
    });

    case PUT_CREATED_USER_PROJECT: return ({
      ...state, 
      myProjects: {...state.myProjects, data: [...state.myProjects.data, ...action.payload]} //[state.myProjects, action.payload]
    });
    
    case PUT_DELETED_USER_PROJECT: return ({
      ...state, 
      myProjects: [state.myProjects, action.payload]
    });
    
    case PUT_ADDED_USER_IN_PROJECT: 
      const newData = state.myProjects.data.map(project => {
        
        if (project.id === action.payload.data['project_user']['project_id']) 
          return {...project, users: {...project.users, ...[action.payload.data.user]}}
        
        return project;
      });
      
      console.log(newData, 'Новая дата');

      const newState = {...state, myProjects: {...state.myProjects, data: {...state.myProjects.data, ...newData}}};
      return ({
        newState
      });
      break;

    default: return state;  
  }
}
