import { takeEvery, put, call } from 'redux-saga/effects';
import { SEND_REGISTER_DATA, SEND_LOGIN_DATA, putRegisterData, putFailedRegisterData, putFailedLoginData, putLoginData } from './actions';
import { addNotify } from '../notify/actions';
import * as config from '../config';


function fetchRegisterData(data) {

  return fetch(config.URL_REGISTER_API, { 
    method: 'POST', 
    body: JSON.stringify(data),
    headers: {
      'Content-Type': 'application/json'
    },
  })
    .then(response => response.status == 404 || response.ok ? response.json() : Promise.reject(response))
    .catch((e) => console.error(e));
  

}


function fetchLoginData(data) {

  const newData = {
    username: data.email,
    password: data.password,
    grant_type: 'password',
    client_id: config.CLIENT_ID,
    client_secret: config.CLIENT_SECRET
  }

  console.log(JSON.stringify(newData));

  return fetch(config.URL_LOGIN_API, { 
    method: 'POST', 
    body: JSON.stringify(newData),
    headers: {
      'Content-Type': 'application/json'
    },
  })
    .then(response => response.status == 404 || response.ok ? response.json() : Promise.reject(response))
    .catch((e) => console.error(e));
  

}

function* workerRegisterData(action) {
  
	const data = yield call(fetchRegisterData, action.payload);
  
	if (data.success) 	
    yield put(putRegisterData(data));
  else 
    yield put(putFailedRegisterData(data));
	
  
}

function* workerLoginData(action) {
  console.log('работает', action.payload)
	const data = yield call(fetchLoginData, action.payload);
  
	if (data) 	
    yield put(putLoginData({ ...data, name: action.payload.email }));
  else 
    yield put(putFailedLoginData(data));
	
  
}

export function* watchRegisterData() {
  yield takeEvery(SEND_REGISTER_DATA, workerRegisterData);
}

export function* watchLoginData() {
  yield takeEvery(SEND_LOGIN_DATA, workerLoginData);
}

