import React, {Component} from 'react';


class SidebarItem extends Component {
  changeItem() {
    console.log(this.parentLi)
  }

  render() {
    return (
      <li className="left-menu__item" ref={li => this.parentLi = li} onClick={this.changeItem}>
        <a className="left-menu__link" href={this.props.item.link}>{this.props.item.text}</a>
          <ul className="left-menu__second-list">
          {console.log(this.props.childs)}
          {this.props.childs.map(child => {
            return(
              <li className="left-menu__second-list-item" ref={li => this.childLi = li}>
                <a className="left-menu__second-list-link" href={child.link}>{child.text}</a>
              </li>
            );
          })}
        </ul>
      </li>
    );
  }
}
export default SidebarItem;
