import { 
  PUT_GOTTEN_USER_PROJECTS,
} from './actions';

const initialState = {
  projectsList: [],
};

export const projectsReduser = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {

    case PUT_GOTTEN_USER_PROJECTS: return ({
      ...state, 
      projectsList: action.payload.data
    });

    default: return state;  
  }
}
