import React, {Component} from 'react';
import './SnippetNav.sass';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoffee } from '@fortawesome/free-solid-svg-icons';

const snippets = [
  {title: 'html', type: 'html', id: '1', value: ''},
  {title: 'js', type: 'js', id: '2', value: ''}
];

class SnippetNav extends Component {
  render() {
    return(
      <nav class="top-content-nav">
      <ul class="top-list">
        {snippets.map(snippet => (<li class="top-list__item"><a class="top-list__link active" href="#">{snippet.title}</a></li>))}
        
        <li class="top-list__item">
          <a href="block:#add-snippet" class="top-list__button">+</a>

          <div id="add-snippet" class="top-list__add-block mini-block">
            <div class="mini-block__title">Добавить сниппет</div>
            
            <div class="tags">
              <div class="tag">html<FontAwesomeIcon className="tag__icon" icon={faCoffee} /></div>
              <div class="tag">js</div>
              <div class="tag">css</div>
            </div>

            <form class="top-list__form">
              <input class="top-list__form-input bottom-line-input" type="text" placeholder="Название" />

              <div class="top-list__form-buttons">
                <button class="btn-grey btnclr close-block top-list__form-button">Отмена</button>
                <button class="btn-grey btnclr top-list__form-button">Добавить</button>		
              </div>						
            </form>

          </div>
        </li>
      </ul>
      
    </nav>

    );
  }
}
export default SnippetNav;
