import React, {useEffect, Fragment} from 'react';
import {Switch, Route} from 'react-router-dom';
import {
  Card,
  CardTitle,
  CardSubTitle,
  CardButtons,
  Members,
  Cards, 
  CardСlose
} from '../UI/Card/Card';
import Loader from '../UI/Loader/Loader';
import MiniButton from '../UI/MiniButton/MiniButton';
import SimpleButton from '../UI/SimpleButton/SimpleButton';



const Projects = (props) => {
  return (
    <Switch>
      <Route exact path="/"></Route>
      <Route path="/my">
        <MyProjects 
          myProjects={props.myProjects} 
          getUserProjects={props.getUserProjects}
          deleteUserProject={props.deleteUserProject}
        />
      </Route>
      <Route path="/notmy">not my</Route>
    </Switch>
  );
}

const MyProjects = (props) => {
  console.log(props.getUserProjects)
  useEffect(() => {
    if (props.myProjects.success === undefined)
      props.getUserProjects();    
  })

  if (props.myProjects !== undefined && props.myProjects.data !== undefined) {
    return (
      <Cards>
        {props.myProjects.data.map((project) => {
          return(
            <Card key={project.id}>
              <CardСlose onClick={() => {props.deleteUserProject(project.id)}}/>
              <CardTitle>{project.name}</CardTitle>
              <CardSubTitle>Участники:</CardSubTitle>
              
              <Members members={[{name: 'Oleg Koshobvich'}, {name: 'вы'}]} />
  
              <CardButtons>
                <SimpleButton mixinClass="card__button" theme="purple">Открыть</SimpleButton>
                <SimpleButton mixinClass="card__button" theme="mint">Добавить участника</SimpleButton>
              </CardButtons>
            </Card>
          );
        })}
      </Cards>
    );
  } else if (props.myProjects.success) {
    return 'Проектов пока нет(, добавте новый';
  } else {
    return 'Произошла ошибка сервера';
  }

  
}

export default Projects;
